    private List<Map<String, Object>> getData() {
        List<Map<String, Object>> list = new ArrayList<>();
        Map<String, Object> map;
        for (int i = 0; i < 50; i++) {
            map = new HashMap<>();
            map.put("title", "G" + i);
            list.add(map);
        }
        return list;
