		// Define 'where' part of query.
		String selection = FeedEntry._ID + " = ?";
		// Specify arguments in placeholder order.
		String[] selectionArgs = { String.valueOf(rowId) };
		// Issue SQL statement.
		int count = db.delete(FeedEntry.TABLE_NAME, selection, selectionArgs);
		db.close();
		return count;
	}
}

