	@Override
	public boolean onCreateOptionsMenu(Menu menu) {

		// Inflate the menu; this adds items to the action bar if it is present.
		getMenuInflater().inflate(R.menu.activity_main, menu);
		initSearch(menu);
		return true;
	}

	private VideoFragment videoFragment;

	/**
	 * 
	 * 
	 * @param menu
	 */
	private void initSearch(Menu menu) {
		MenuItem item = menu.findItem(R.id.menu_search);
		View view = item.getActionView();
		final EditText search = (EditText) view.findViewById(R.id.search);
		MenuItemCompat.setOnActionExpandListener(item,
				new OnActionExpandListener() {
					@Override
					public boolean onMenuItemActionCollapse(MenuItem item) {
						// Toast.makeText(MainDrawerActivity.this, "",
						// Toast.LENGTH_SHORT).show();
						search.clearFocus(); // 
						AppUtil.hideSoftInput(MainDrawerActivity.this);
						if (search.getText().length() > 0) {// 
							videoFragment.searchVideoTable(false, "");//
						}
						return true;
					}

					@Override
					public boolean onMenuItemActionExpand(MenuItem item) {
						// Toast.makeText(MainDrawerActivity.this, "",
						// Toast.LENGTH_SHORT).show();
						search.requestFocus(); // 
						/**
						 * 
						 */
						InputMethodManager m = (InputMethodManager)     getSystemService(Context.INPUT_METHOD_SERVICE);
						m.toggleSoftInput(0, InputMethodManager.HIDE_NOT_ALWAYS);

						return true;
					}
				});
		search.setOnEditorActionListener(new OnEditorActionListener() {

			@Override
			public boolean onEditorAction(TextView v, int actionId,
					KeyEvent event) {
				if (actionId == EditorInfo.IME_ACTION_SEARCH) {
					if (search.getText().length() > 0) {// 
						AppUtil.hideSoftInput(MainDrawerActivity.this);
						videoFragment.searchVideoTable(true, search.getText()
								.toString());
					}
					return true;
				}
				return false;
			}
		});
	}
