    class SaveImageToGalleryTask extends AsyncTask<Objects, Objects, Integer> {
        @Override
        protected Integer doInBackground(Objects... params) {
            Bitmap bitmap = null;

            try {

                bitmap = Picasso.with(GirlImageDetailActivity.this).load("URL").get();
            } catch (IOException e) {
                e.printStackTrace();
            }
            if (bitmap == null) {
                return 0;
            }
            File appDir = new File(Environment.getExternalStorageDirectory(), "hua");
            if (!appDir.exists()) {
                appDir.mkdir();
            }
            final String fileName = ""+ ".png";
            File file = new File(appDir, fileName);
            if (file.exists()) {
                return 2;
            } else {
                try {
                    FileOutputStream fileOutputStream = new FileOutputStream(file);
                    bitmap.compress(Bitmap.CompressFormat.PNG, 100, fileOutputStream);
                    fileOutputStream.flush();
                    fileOutputStream.close();

                    // 
                    MediaStore.Images.Media.insertImage(GirlImageDetailActivity.this.getContentResolver(), file.getAbsolutePath(), fileName, null);

                    // 
                    Intent intent = new Intent(Intent.ACTION_MEDIA_SCANNER_SCAN_FILE, Uri.parse("file://" + file.getAbsolutePath()));
                    sendBroadcast(intent);
                    return 1;
                } catch (Exception e) {
                    e.printStackTrace();
                    return 0;
                }

            }
        }

        @Override
        protected void onPostExecute(Integer integer) {
            super.onPostExecute(integer);
            switch (integer) {
                case 0:
                    Snackbar.make(detailLayout, "", Snackbar.LENGTH_SHORT).show();
                    break;
                case 1:
                    File appDir = new File(Environment.getExternalStorageDirectory(), "hua");
                    Snackbar.make(detailLayout, "" + appDir.getAbsolutePath(), Snackbar.LENGTH_SHORT).show();
                    break;
                case 2:
                    Snackbar.make(detailLayout, "", Snackbar.LENGTH_SHORT).show();
                    break;
            }
        }
    }
