                    Snackbar.make(detailLayout, "", Snackbar.LENGTH_SHORT).show();
                    break;
                case 1:
                    File appDir = new File(Environment.getExternalStorageDirectory(), "hua");
                    Snackbar.make(detailLayout, "" + appDir.getAbsolutePath(), Snackbar.LENGTH_SHORT).show();
                    break;
                case 2:
                    Snackbar.make(detailLayout, "", Snackbar.LENGTH_SHORT).show();
                    break;
            }
        }
    }
