```java
   private List<Map<String, Object>> mData;
   private ListView mListView;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        mData = getData();
        MyAdapter adapter = new MyAdapter(this);
        mListView = (ListView) findViewById(R.id.listView);
        mListView.setAdapter(adapter);
    }

    private List<Map<String, Object>> getData() {
        List<Map<String, Object>> list = new ArrayList<>();
        Map<String, Object> map;
        for (int i = 0; i < 50; i++) {
            map = new HashMap<>();
            map.put("title", "G" + i);
            list.add(map);
        }
        return list;
    }


    public class MyAdapter extends BaseAdapter {
        private LayoutInflater mInflater;

        public MyAdapter(Context context) {
            this.mInflater = LayoutInflater.from(context);
        }

        @Override
        public int getCount() {
            return mData.size();
        }

        @Override
        public Object getItem(int arg0) {
            return mData.get(arg0);
        }

        @Override
        public long getItemId(int arg0) {
            return arg0;
        }

        @Override
        public View getView(final int position, View convertView, ViewGroup parent) {
            Log.d("wxl", "getView=" + position);
            ViewHolder holder = null;
            if (convertView == null) {
                holder = new ViewHolder();
                convertView = mInflater.inflate(R.layout.listview_item, null);
                holder.textview = (TextView) convertView.findViewById(R.id.textview);
                convertView.setTag(holder);
            } else {
                holder = (ViewHolder) convertView.getTag();
            }
            holder.textview.setText((String) mData.get(position).get("title"));
            convertView.setOnClickListener(new View.OnClickListener() {

                @Override
                public void onClick(View v) {
                    updateItemView(position);//
                }
            });


            return convertView;
        }

        public class ViewHolder {
            public TextView textview;
        }
    }

    public void updateItemView(int position) {
        //
        int visiblePosition = mListView.getFirstVisiblePosition();
        //view
        int index = position - visiblePosition;
        if (index >= 0) {
            //itemview
            View view = mListView.getChildAt(index);
            //viewholder
            MyAdapter.ViewHolder holder = (MyAdapter.ViewHolder) view.getTag();
            //
            holder.textview.setText("");
            //
            mData.get(index).put("title", "");
        }
    }
}
