	                        info.getLac();
	                        info.getNetworkType();
	                        info.getPsc();
	                        // 获取邻居小区信号强度
	                        info.getRssi();
	                }
	                return "手机号码:" + phoneNum + "\n" + "服务商：" + ProvidersName + "\n"
	                                + "IMEI：" + Imei;

