	protected void onDraw(Canvas paramCanvas) {
		paramCanvas.drawColor(this.paperColor);
		int i = getLineCount();// 
		int j = getHeight();// TextView
		int k = getLineHeight();// TextView
		int m = j / k + 1;// 
		if (i < m)
			i = m;
		int n = getCompoundPaddingTop();
